nction rg_cashback_uninstall() {	rg_cashback_remove_directory_structures();	include( RGCASHBACK__PLUGIN_DIR . 'includes/rg-uninstall.php' );}register_uninstall_hook( __FILE__, 'rg_cashback_uninstall' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/core.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/ajax-calls.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/pages/main-page.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/pages/import-cashback-page.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/pages/import-banners-page.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/pages/stores-listing-page.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/pages/categories-listing-page.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/pages/banners-listing-page.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/pages/cashback-listing-page.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/pages/cashout-listing-page.php' );require_once( RGCASHBACK__PLUGIN_DIR . 'includes/pages/cashback-setting-page.php' );?>